$(function(){var e=$("[data-notation]"),t=$("<a/>",{"class":"notes-tab-control",href:"#"}).appendTo("body"),n=$("<section/>",{"class":"notes-tab-pane"}).appendTo("body"),r=$(this).attr("title");n.html("<h1>"+r+"</h1>");var i=$('<form class="form-notes-display"><input type="checkbox" id="note-anchors-display-toggle" checked="checked" >Show note markers</form><h2>Page Notes</h2>');i.appendTo(n);var s=$("<dl/>",{"class":"notes-list"}).appendTo(n);for(var o=0;o<e.length;o++)s.append('<dt class="note-count note-count-'+(o+1)+'">'+(o+1)+"</dt>"+'<dd class="note-body note-body-'+(o+1)+'">'+$(e[o]).data("notation")+"</dd>");for(o=0;o<e.length;o++)$(e[o]).prepend('<span class="notes-anchor"><figure>'+(o+1)+"</figure></span>");var u=$(".notes-anchor");$(t).add(u).click(function(e){t.toggleClass("is-open");n.toggleClass("is-open");e.preventDefault()});$("#note-anchors-display-toggle").click(function(){console.log(u);u.toggleClass("notes-hide")})});